import React, { Component } from "react";
import {View, Text, StyleSheet, Image, Button, Alert, ImageBackground, TouchableOpacity, TextInput, ScrollView, }
from 'react-native'
import {AsyncStorage} from '@react-native-async-storage/async-storage'
import API from "./API"
import Header from './header'
import Body from './body'

class Login extends Component{

    constructor(props){
        super(props)
        console.log("Propiedades :" + props)
        this.state = {
            user: '',
            password: '',
        }
    }

    // método anónimo?? :D 
    navegar = async (param) => {
        console.log(param)
        if(param == 'UserProfile'){
            let userLogin = {
                user : this.state.user,
                perm: true
            }
            AsyncStorage.setItem("userLogin", JSON.stringify(userLogin))
            this.props.navigation.navigate(params)
        }
        else{
            this.props.navigation.navigate(param)
        }
    }

    logear = () => {
      this.props.navigation.navigate('UserProfile')
    }

    render(){
        return(
            <ImageBackground source = {require('../images/fondo.jpg')} style={styles.container}>
                <Header />
                <View style={styles.container}>
                  <TextInput placeholder = 'Usuario' placeholderTextColor='white' maxLength = {8}
                  style = {[styles.styleInput, {marginTop: 10, width: 200}]}
                  onChangeText = {(username) => {this.setState(username)}}
                  />
                  <TextInput placeholder = 'Contraseña' placeholderTextColor='white' maxLength = {8}
                  secureTextEntry={true}
                  style = {[styles.styleInput, {marginTop: 20, width: 200}]}
                  onChangeText = {(password) => {this.setState(username)}}
                  />
                  <View style = {[styles.styleBoton, {marginTop: 50}]}>
                    <Button title="Login" onPress={this.navegar}/>
                  </View>

                  <View style = {styles.styleText}>
                    <Text style={{color: 'white', fontSize: 14}}> Or </Text>
                  </View>

                  <View style = {[styles.styleBoton, {marginTop: 15}]}>
                    <Button title="Create User" />
                  </View>

                </View>    
            </ImageBackground>    
        )
    }

}

const styles = StyleSheet.create({
    container : {
      flex: 1,
      // justifyContent: 'center',
      alignItems: 'center'
    },
    styleText:{
      marginTop: 10,
      alignItems: 'center'
    },
    styleBoton: {
      width: 130
    },
    styleInput: {
      borderWidth: 1,
      color: 'white',
      borderColor: 'white',
      padding: 5
    }
  })
export default Login
