import 'react-native-gesture-handler';
import React, {Component} from 'react'
import {View, Text, StyleSheet, Image, Button, Alert, ImageBackground, 
  TouchableOpacity, TextInput, ScrollView } from 'react-native'
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import SignInScreen from './src/SignInScreen'
import UserProfile from './src/UserProfile'
import Loading from './src/SplashScreen'
import {AsyncStorage} from '@react-native-async-storage/async-storage'



var Stack = createStackNavigator();
function CVRP(){

  const estadoInicial = {
    isLoading: true,
    isSignout: false,
    userToken: null,
  }

  const reducer = (prevState, action) => {
    switch (action.type) {
      case 'RESTORE_TOKEN':
        return {
          ...prevState,
          userToken: action.token,
          isLoading: false,
        };
      case 'SIGN_IN':
        return {
          ...prevState,
          isSignout: false,
          userToken: action.token,
        };
      case 'SIGN_OUT':
        return {
          ...prevState,
          isSignout: true,
          userToken: null,
        };
    }

  }

  const [state, dispatch] = React.useReducer(reducer, estadoInicial)



  React.useEffect(() => {
    // Fetch the token from storage then navigate to our appropriate place
    //Define un método ásincrono para el loading
    const bootstrapAsync = async () => {
      let userToken;

      try {
        userToken = await AsyncStorage.getItem('userToken');
      } catch (e) {
        // Restoring token failed
        console.log("Algo anduvo mal :(")
      }

      // After restoring token, we may need to validate it in production apps

      // This will switch to the App screen or Auth screen and this loading
      // screen will be unmounted and thrown away.
      dispatch({ type: 'RESTORE_TOKEN', token: userToken });
    };

    bootstrapAsync();
  }, []);



  if (this.state.isLoading) {
    // We haven't finished checking for the token yet
    return <SplashScreen />;
  }

  return (
    <Stack.Navigator>
      {this.state.userToken == null ? (
        // No token found, user isn't signed in
        <Stack.Screen
          name="SignIn"
          component={SignInScreen}
          options={{
            title: 'Sign in',
            // When logging out, a pop animation feels intuitive
            // You can remove this if you want the default 'push' animation
            animationTypeForReplace: state.isSignout ? 'pop' : 'push',
          }}
        />
      ) : (
        // User is signed in
        <Stack.Screen name="Home" component={HomeScreen} />
      )}
    </Stack.Navigator>
  );
}

  export default CVRP
