import React, { Component } from 'react';
import { ImageBackground, StyleSheet, View, Text, TextInput, Image, Button, Alert} from 'react-native';
import { Container} from 'native-base';
// import Cabecera from './header'
// import Cuerpo from './body'

const image = { uri: "../images/fondo.jpg" };

class Login extends Component {
  loginCheck = () => Alert.alert('Simple Button pressed')
  createUser = () => {
    this.props.navigation.navigate('CreateUser')
  }

  render() {
    return (
      <Container style={{backgroundColor: 'black'}}>
        <ImageBackground source = {image} style={styles.container}>

        {/* Cabecera */}
        <View style={styles.containerHeader}>
          <View style={styles.headerLeft}>
            <Image source={require('../assets/favicon.png')} 
            style={styles.logo}></Image>
          </View>
            <Text style={ {color: 'white'} } > Aqui iría un nombre picante </Text>
        </View>

        {/* Cuerpo */}
        <View style={styles.container}>
          <TextInput placeholder = 'Usuario' placeholderTextColor='white' maxLength = {8}
          style = {[styles.styleInput, {marginTop: 10, width: 200}]}
          />
          <TextInput placeholder = 'Contraseña' placeholderTextColor='white' maxLength = {8}
          secureTextEntry={true}
          style = {[styles.styleInput, {marginTop: 20, width: 200}]}
          />
        
        <View style = {[styles.styleBoton, {marginTop: 50}]}>
          <Button title="Login" onPress={this.loginCheck}/>
        </View>

        <View style = {styles.styleText}>
          <Text style={{color: 'white', fontSize: 14}}> Or </Text>
        </View>

        <View style = {[styles.styleBoton, {marginTop: 15}]}>
          <Button title="Create User" onPress={this.createUser}/>
        </View>

        </View>

        </ImageBackground>
      </Container>
    );
  }
}

const styles = StyleSheet.create({
  container : {
    flex: 1,
    // justifyContent: 'center',
    alignItems: 'center'
  },
  containerHeader: {
    flex: 0.5,
    marginTop: 50,
    justifyContent: 'center',
    alignItems: 'center'
  },
  styleText:{
    marginTop: 10,
    alignItems: 'center'
  },
  styleBoton: {
    width: 130
  },
  styleInput: {
    borderWidth: 1,
    color: 'white',
    borderColor: 'white',
    padding: 5
  }

})

export default Login
